mapper.select:
SELECT COUNT(*) FROM (SELECT (SELECT name FROM msmartshop.tblmember WHERE id = a.id) AS NAME, a.id AS userID,max(a.date) AS date, COUNT(a.id) AS cnt FROM franch_use_tbl AS a JOIN franch_tbl AS b ON a.sellerCd = b.sellerCd
  
   and DATE(DATE) BETWEEN #{startDate} AND #{endDate} 
  
  
   and DATE(DATE) BETWEEN #{startDate_1} AND #{endDate_1} 
  
group BY id ORDER BY cnt DESC) AS b;
